        Dynamsoft.DWT.EnumDWT_ImageType.IT_PNG,
        Dynamsoft.DWT.EnumDWT_UploadDataFormat.Binary, 
        &quot;WebTWAINImage.png&quot;, 
        ()=&gt;{
          res();
        }, (errCode, errString, responseStr) =&gt; { 
          rej({
            code: errCode,
            message: errString
          });
        });
    } else {
      rej({
        code: -1,
        message: &quot;There is no image in buffer.&quot;
      });
    }
  }
}


