&lt;template&gt;
  &lt;div class="hello"&gt;
    &lt;h1&gt;&lt;/h1&gt;
    &lt;select id="sources" v-model="selectedIndex"&gt;
      &lt;option
        v-for="(source, index) in sourceList"
        v-bind:value="index"
        :key="index"
      &gt;
        {{ source.displayName }}
      &lt;/option&gt;
    &lt;/select&gt;
    &lt;button @click="acquireImage()"&gt;Acquire Images&lt;/button&gt;
    &lt;button @click="openImage()"&gt;Open Documents&lt;/button&gt;
    &lt;br /&gt;
    &lt;br /&gt;
    &lt;div v-bind:id="containerId"&gt;&lt;/div&gt;
  &lt;/div&gt;
